mapper.update:
UPDATE items
SET name = #{name}, price = #{price}, inventory = #{inventory}, status = #{status}
WHERE id = #{id}

DELETE FROM items
WHERE id IN (

    #{item}

)

mapper.select:
SELECT id, seller_id, name, price, inventory, status
FROM items
WHERE id = #{id}

SELECT id, seller_id, name, price, inventory, status
FROM items
WHERE seller_id = #{sellerId}
