mapper.select:
SELECT id, seller_id, category_id, name, price, inventory, status
FROM products
WHERE id = #{id}

SELECT id, seller_id, category_id, name, price, inventory, status
FROM products
WHERE seller_id = #{sellerId}

SELECT id, seller_id, category_id, name, price, inventory, status
FROM products
WHERE category_id = #{categoryId}
LIMIT #{limit} OFFSET #{offset}
ORDER BY ASC
